function GetSelectedText (){
  var userSelection, ta;
  if (window.getSelection && document.activeElement){
    if (document.activeElement.nodeName == "TEXTAREA" ||
        (document.activeElement.nodeName == "INPUT" &&
        document.activeElement.getAttribute("type").toLowerCase() == "text")){
      ta = document.activeElement;
      userSelection = ta.value.substring(ta.selectionStart, ta.selectionEnd);
    } else {
      userSelection = window.getSelection();
    }
    document.getElementById("seltext").innerHTML += "Selection="+userSelection.toString()+" [1]";
  } else {
    // all browsers, except IE before version 9
    if (document.getSelection){       
        userSelection = document.getSelection();
        document.getElementById("seltext").innerHTML += "Selection="+userSelection.toString()+" [2]";
    }
    // IE below version 9
    else if (document.selection){
        userSelection = document.selection.createRange();
        document.getElementById("seltext").innerHTML += "Selection="+userSelection.text+" [3]";
    }
  }
}



